mapper.select:
select eduno, eduName, eduContent, eduStatus, eduApp, eduAddr, educator, eduPhone, eduFile, eduDetailFile, edusysdate from (select rownum n, eduno, eduName, eduContent, eduStatus, eduApp, eduAddr, educator, eduPhone, eduFile, eduDetailFile, edusysdate from (select * from education where id = #{id}
and eduName like '%'||#{search}||'%'
and eduContent like '%'||#{search}||'%'
and eduApp like '%'||#{search}||'%'
order by eduNo desc)) a where a.n between #{start} and #{end}
