mapper.select:
SELECT *
FROM (
SELECT ROWNUM as row_num, l.*
FROM ( SELECT * FROM lecture WHERE lecNO is NOT NULL ORDER BY lecNO) l
)
WHERE row_num BETWEEN #{start} AND #{end}

SELECT *
FROM (
SELECT ROWNUM as row_num, e.*
FROM ( SELECT * FROM education WHERE eduNO is NOT NULL ORDER BY eduno) e
)
WHERE row_num BETWEEN #{start} AND #{end}

SELECT *
FROM (
SELECT ROWNUM as row_num, t.*
FROM ( SELECT * FROM trainingRequest WHERE reqNo is NOT NULL ORDER BY reqNo) t
)
WHERE row_num BETWEEN #{start} AND #{end}
